import { View, Text, TextInput, StyleSheet, Pressable, Image } from "react-native";



function SignInScreen({ navigation }) {

    function signInHandler() {
        navigation.navigate("HomeScreen");
    }

    return (
        <View style={styles.rootContainer}>
            <View  >
                <View style={styles.inputContainer}>

                    <Image source={require("../assets/images/clg_logo.png")} />

                    <Text style={styles.mainText}>Welcome Message !</Text>

                    <Text style={styles.subtitleText} >Please signin to your account using the provided password</Text>

                    <View style={styles.parentEmailPasswordContainer}>
                        <View style={styles.emailContainer}>
                            <TextInput style={styles.emailText} placeholder="E-Mail" blurOnSubmit={true} />
                        </View>

                        <View style={styles.passswordContainer}>
                            <TextInput style={styles.passwordText} placeholder="Password" visiblel />
                        </View>
                    </View>

                    <View style={styles.buttonContainer}>


                        <View style={styles.mainpressable}>
                            <Pressable android_ripple={{ color: '#ccc' }} style={styles.pressable} onPress={signInHandler}>
                                <Text style={styles.pressableText}>Sign In</Text>
                            </Pressable>
                        </View>



                        <View style={styles.footer}>
                            <Text style={{ fontStyle: 'italic' }}>I hereby abide by the restrictions and proceed</Text>
                        </View>

                    </View>
                </View>
            </View>
        </View>
    );
}

export default SignInScreen;

const styles = StyleSheet.create({
    rootContainer: {
        flex: 1,
        marginTop: 50,
        backgroundColor: "#F5FCFF"

    },

    inputContainer: {
        justifyContent: 'center',
        alignItems: 'center'

    },

    mainText: {
        fontWeight: 'bold',
        marginBottom: 10,
        fontSize: 30,
        marginTop: 10

    },

    subtitleText: {
        marginBottom: 25
    },

    parentEmailPasswordContainer: {

    },

    emailContainer: {
        marginBottom: 30,
        borderWidth: 1,
        borderRadius: 10
    },

    passswordContainer: {
        borderWidth: 1,
        width: 250,
        borderRadius: 10
    },

    emailText: {
        paddingLeft: 10
    },

    passwordText: {
        paddingLeft: 10
    },

    sideText: {
        marginTop: 10
    },

    bottomContainer: {



    },

    buttonContainer: {
        flexDirection: 'column'

    },



    mainpressable: {
        overflow: 'hidden',
        flexDirection: 'column',
        borderWidth: 1,
        borderRadius: 20,
        marginTop: 20


    },

    pressable: {
        elevation: 2,
        backgroundColor: "#1640D6"
    },

    pressableText: {
        padding: 15,
        textAlign: 'center',
        color: 'white',
        fontSize: 20
    },

    footer: {

        justifyContent: 'center'
    }
})
