import React from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { Ionicons } from '@expo/vector-icons';
import Home from '../studentNavScreen/Home';
import Peer from '../studentNavScreen/Peer';
import Classroom from '../studentNavScreen/Classroom';


function TeacherMain() {
  const Tab = createBottomTabNavigator();

  return (
    <Tab.Navigator
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, size, color }) => {
          let iconName;

          if (route.name === 'Home') {
            iconName = focused ? 'home' : 'home-outline';
            size = focused ? size + 8 : size+5;
          } else if (route.name === 'Peer') {
            iconName = focused ? 'person-add' : 'person-add-outline';
            size = focused ? size + 8 : size+5;
          } else if (route.name === 'Classroom') {
            iconName = focused ? 'school' : 'school-outline';
            size = focused ? size + 8 : size+5;
          }

          return <Ionicons name={iconName} size={size} color="#020202" />;
        },
      })}
      tabBarOptions={{
        activeTintColor: 'black',
        inactiveTintColor: 'black',
        showLabel: false,
        style: {
          backgroundColor: 'black',
          height: 60,
        },
      }}
    >
      <Tab.Screen name="Home" component={Home} />
      <Tab.Screen name="Peer" component={Peer} />
      <Tab.Screen name="Classroom" component={Classroom} />
      
    </Tab.Navigator>
  );
}

export default TeacherMain;
